import React, { Component } from 'react'
class Movie extends Component {
    constructor(props) {
        super(props);
        this.state = {  
            list:[
                { id: 0, name: "Inception", rating: 1, image:"data:image/jpeg;base64,"},
                { id: 1, name: "In the Name of the King", rating: 4, image: "https://www.flixwatch.co/wp-content/uploads/70043309-800x450.jpg" },
                { id: 2, name: "Dachera", rating: 2, image: "https://static.wilout.com/e6f49b280b84db7df5a0f22953d33d69-background-1554803470207.jpg" },
                { id: 3, name: "The Challenge", rating: 5, image: "https://wikiseries.co/images/posters/wart23rnyGluvHm2Zr2bS1FK0Mg.jpg" },
                { id: 4, name: "Joker", rating: 3, image: "data:image/jpeg;base64," }
        ],
        searchStar:5,
        searchName:"",
        display:false
        }
    }
    setStar=(e)=>{
        this.setState({
            searchStar:e
        })
    }
    setName=(e)=>{
        this.setState({
            searchName:e.target.value
        })
    }
    addItem=()=>{
        let newItem={id:Math.random(), name:this.state.name, rating:this.state.rating, image:this.state.image}
        this.setState({
            list:[...this.state.list, newItem]
        })
    }
    render() { 
        return ( <div>
                    <div className="search" onChange={this.setName}>
                        <input type="text" />
                    <div className="star">
                            <span onClick={(e)=>this.setStar(1)}>⋆</span>
                            <span onClick={(e) =>this.setStar(2)}>⋆</span>
                            <span onClick={(e) =>this.setStar(3)}>⋆</span>
                            <span onClick={(e) =>this.setStar(4)}>⋆</span>
                            <span onClick={(e) =>this.setStar(5)}>⋆</span>
                        </div>
                    </div>
                <div className="movies">
                    {this.state.list.filter((el) => el.name.toUpperCase().includes(this.state.searchName.toUpperCase()) && el.rating <= this.state.searchStar).map(el => {
                        return (
                            <div key={el.id} className='movie'>
                                <p className="stars">{"".padEnd(el.rating, "⋆")}</p>
                                <img src={el.image} alt="im-movie" />
                                <p className="name">{el.name}</p>
                            </div>)
                    }
                    )}
                </div>
               

                 <button className="btn1" onClick={() => this.setState({ display: !this.state.display })}>+</button>
                <div className="form">
                    <div className={this.state.display ? 'display' : 'none'}>

                    <button className="close" onClick={() => this.setState({ display: !this.state.display })}>X</button>
                            <input type="text" onChange={(e)=>this.setState({name:e.target.value})}/>
                            <input type="number" onChange={(e) => this.setState({ rating: e.target.value })}/>
                            <input type="text" onChange={(e) => this.setState({image: e.target.value })}/>
                            <button className="btn" onClick={this.addItem}>Add</button>

                    </div>
                </div>
                

                 </div>
                );
    }
}
 
export default Movie;